import React from "react";
import Sidebar from "../components/Sidebar";
import { Outlet } from "react-router";
import { Box, Grid } from "@mui/material";
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import Avatar from '@mui/material/Avatar';

const AdminLayout = () => {
  return (
    <Box sx={{ display: 'flex' }}>
      {/* AppBar with full width */}
      <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
        <Toolbar>
          <Typography variant="h6" noWrap sx={{ flexGrow: 1 }}>
            WinCric
          </Typography>
          <IconButton edge="end" color="inherit">
            <Avatar alt="Profile Icon" src="/path-to-profile-image.jpg" />
          </IconButton>
        </Toolbar>
      </AppBar>

      {/* Sidebar */}
      <Sidebar />

      {/* Main content area */}
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          marginLeft: '240px', // Adjust based on Sidebar width
          marginTop: '64px', // Adjust based on AppBar height
        }}
      >
        <Toolbar /> {/* Empty Toolbar to offset the content for AppBar */}
        <Outlet />
      </Box>
    </Box>
  );
};

export default AdminLayout;
