import React, { useState, useEffect } from "react";
import { Grid, Card, CardContent, Typography, IconButton, CircularProgress } from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";

// Define a base URL for the API
const API_URL = 'http://0.0.0.0:3000/videos';

function Video() {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch videos from the API
    fetch(API_URL)
      .then(response => response.json())
      .then(data => {
        setVideos(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching videos:', error);
        setLoading(false);
      });
  }, []);

  const handleAddVideo = () => {
    // Implement functionality to add a new video
    const newVideo = { id: Math.random(), title: `New Video ${videos.length + 1}` };
    setVideos([...videos, newVideo]);
  };

  const handleEditVideo = (id) => {
    // Implement functionality to edit a video
    console.log(`Editing video with ID ${id}`);
  };

  const handleDeleteVideo = (id) => {
    // Implement functionality to delete a video
    setVideos(videos.filter((video) => video.id !== id));
  };

  if (loading) {
    return <CircularProgress />;
  }

  return (
    <Grid container spacing={3}>
      {videos.map((video) => (
        <Grid item xs={12} sm={6} md={4} lg={3} key={video.id}>
          <Card>
            <CardContent>
              <Typography variant="h6" component="div">
                {video.title}
              </Typography>
              <IconButton onClick={() => handleEditVideo(video.id)}>
                <EditIcon />
              </IconButton>
              <IconButton onClick={() => handleDeleteVideo(video.id)}>
                <DeleteIcon />
              </IconButton>
            </CardContent>
          </Card>
        </Grid>
      ))}
      <Grid item xs={12} sm={6} md={4} lg={3}>
        <Card>
          <CardContent>
            <IconButton onClick={handleAddVideo}>
              <AddIcon />
            </IconButton>
            <Typography variant="subtitle1" component="div">
              Add Video
            </Typography>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
}

export default Video;
