import React, { useState, useEffect } from "react";
import {
  Grid,
  Card,
  CardContent,
  Typography,
  IconButton,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";

// Define a base URL for the API
const API_URL = "http://0.0.0.0:3000/videos";

// Validation schema for the video form
const VideoSchema = Yup.object().shape({
  title: Yup.string().required("Title is required"),
  link: Yup.string().url("Invalid URL").required("Link is required"),
});

function Video() {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [openDialog, setOpenDialog] = useState(false);
  const [currentVideo, setCurrentVideo] = useState({ id: null, title: "", link: "" });

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await fetch(API_URL);
      const data = await response.json();
      setVideos(data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching videos:", error);
      setLoading(false);
    }
  };

  const handleAddVideo = () => {
    setCurrentVideo({ id: null, title: "", link: "" });
    setOpenDialog(true);
  };

  const handleEditVideo = (video) => {
    setCurrentVideo(video);
    setOpenDialog(true);
  };

  const handleDeleteVideo = async (id) => {
    try {
      await fetch(`${API_URL}/${id}`, {
        method: "DELETE",
      });
      setVideos(videos.filter((video) => video.id !== id));
    } catch (error) {
      console.error("Error deleting video:", error);
    }
  };

  const handleDialogClose = () => {
    setOpenDialog(false);
    setCurrentVideo({ id: null, title: "", link: "" });
  };

  const handleDialogSave = async (values, { setSubmitting }) => {
    const method = currentVideo.id ? "PUT" : "POST";
    const url = currentVideo.id ? `${API_URL}/${currentVideo.id}` : API_URL;

    try {
      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(values),
      });
      const data = await response.json();

      if (currentVideo.id) {
        setVideos(videos.map((video) => (video.id === currentVideo.id ? data : video)));
      } else {
        setVideos([...videos, data]);
      }
      handleDialogClose();
    } catch (error) {
      console.error(`Error ${currentVideo.id ? "updating" : "adding"} video:`, error);
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return <CircularProgress />;
  }

  return (
    <div>
      <Grid container spacing={3}>
        {videos.map((video) => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={video.id}>
            <Card>
              <CardContent>
                <Typography variant="h6" component="div">
                  {video.title}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="div">
                  {video.link}
                </Typography>
                <IconButton onClick={() => handleEditVideo(video)}>
                  <EditIcon />
                </IconButton>
                <IconButton onClick={() => handleDeleteVideo(video.id)}>
                  <DeleteIcon />
                </IconButton>
              </CardContent>
            </Card>
          </Grid>
        ))}
        <Grid item xs={12} sm={6} md={4} lg={3}>
          <Card>
            <CardContent>
              <IconButton onClick={handleAddVideo}>
                <AddIcon />
              </IconButton>
              <Typography variant="subtitle1" component="div">
                Add Video
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Dialog for Adding/Editing Video */}
      <Dialog open={openDialog} onClose={handleDialogClose}>
        <DialogTitle>{currentVideo.id ? "Edit Video" : "Add Video"}</DialogTitle>
        <Formik
          initialValues={currentVideo}
          validationSchema={VideoSchema}
          onSubmit={handleDialogSave}
          enableReinitialize
        >
          {({ isSubmitting }) => (
            <Form>
              <DialogContent>
                <Field
                  as={TextField}
                  name="title"
                  label="Title"
                  fullWidth
                  margin="dense"
                />
                <Field
                  as={TextField}
                  name="link"
                  label="Link"
                  fullWidth
                  margin="dense"
                />
              </DialogContent>
              <DialogActions>
                <Button onClick={handleDialogClose}>Cancel</Button>
                <Button type="submit" disabled={isSubmitting}>
                  {currentVideo.id ? "Save" : "Add"}
                </Button>
              </DialogActions>
            </Form>
          )}
        </Formik>
      </Dialog>
    </div>
  );
}

export default Video;
