import React, { useState, useEffect } from "react";
import {
    Card,
    CardContent,
    Typography,
    IconButton,
    CircularProgress,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Button,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import { useFormik } from "formik";
import * as Yup from "yup";

// Define a base URL for the API
const API_URL = "http://localhost:3000/packagess";

// Validation schema for the package form
const PackageSchema = Yup.object().shape({
    title: Yup.string().required("Title is required"),
    price: Yup.number().required("Price is required"),
    benefit: Yup.array().required("Benefits are required"),
    time: Yup.string().required("Time is required"),
});

function Packages() {
    const [packages, setPackages] = useState([]);
    const [loading, setLoading] = useState(true);
    const [openDialog, setOpenDialog] = useState(false);
    const [currentPackage, setCurrentPackage] = useState({
        id: null,
        title: "",
        price: "",
        benefit: [],
        time: "",
    });

    useEffect(() => {
        fetchPackages();
    }, []);

    const fetchPackages = async () => {
        try {
            const response = await fetch(API_URL);
            const data = await response.json();
            setPackages(data);
            setLoading(false);
        } catch (error) {
            console.error("Error fetching packages:", error);
            setLoading(false);
        }
    };

    const handleAddPackage = () => {
        setCurrentPackage({ id: null, title: "", price: "", benefit: [], time: "" });
        setOpenDialog(true);
    };

    const handleEditPackage = (pack) => {
        setCurrentPackage(pack);
        setOpenDialog(true);
    };

    const handleDeletePackage = async (id) => {
        try {
            await fetch(`${API_URL}/${id}`, {
                method: "DELETE",
            });
            setPackages(packages.filter((pack) => pack.id !== id));
        } catch (error) {
            console.error("Error deleting package:", error);
        }
    };

    const handleDialogClose = () => {
        setOpenDialog(false);
        setCurrentPackage({ id: null, title: "", price: "", benefit: [], time: "" });
    };

    const handleDialogSave = async (values, { setSubmitting }) => {
        const method = values.id ? "PUT" : "POST";
        const url = values.id ? `${API_URL}/${values.id}` : API_URL;

        try {
            const response = await fetch(url, {
                method,
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(values),
            });
            const data = await response.json();

            if (values.id) {
                setPackages(
                    packages.map((pack) => (pack.id === values.id ? data : pack))
                );
            } else {
                setPackages([...packages, data]);
            }
            handleDialogClose();
        } catch (error) {
            console.error(`Error ${values.id ? "updating" : "adding"} package:`, error);
        } finally {
            setSubmitting(false);
        }
    };

    if (loading) {
        return <CircularProgress />;
    }

    return (
        <>
            <Button
                variant="contained"
                onClick={handleAddPackage}
                style={{ width: "300px" }}
            >
                <AddIcon style={{ marginRight: "5px" }} />
                Add Package
            </Button>
            <div
                style={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                }}
            >
                <div style={{ marginBottom: "20px" }}>
                    {packages.map((pack) => (
                        <Card
                            key={pack.id}
                            style={{ marginBottom: "10px", width: "300px" }}
                        >
                            <CardContent>
                                <Typography variant="h6" component="div">
                                    {pack.title}
                                </Typography>
                                <Typography variant="body2" color="textSecondary" component="div">
                                    Price: {pack.price}
                                </Typography>
                                <Typography variant="body2" color="textSecondary" component="div">
                                    Time: {pack.time}
                                </Typography>
                                <Typography variant="body2" color="textSecondary" component="div">
                                    Benefits: {pack.benefit.join(", ")}
                                </Typography>
                                <IconButton onClick={() => handleEditPackage(pack)}>
                                    <EditIcon />
                                </IconButton>
                                <IconButton onClick={() => handleDeletePackage(pack.id)}>
                                    <DeleteIcon />
                                </IconButton>
                            </CardContent>
                        </Card>
                    ))}
                </div>

                {/* Dialog for Adding/Editing Package */}
                <Dialog open={openDialog} onClose={handleDialogClose}>
                    <DialogTitle>
                        {currentPackage.id ? "Edit Package" : "Add Package"}
                    </DialogTitle>
                    <PackageForm
                        currentPackage={currentPackage}
                        handleDialogSave={handleDialogSave}
                        handleClose={handleDialogClose}
                    />
                </Dialog>
            </div>
        </>
    );
}

function PackageForm({ currentPackage, handleDialogSave, handleClose }) {
    const formik = useFormik({
        initialValues: {
            id: currentPackage.id || null,
            title: currentPackage.title || "",
            price: currentPackage.price || "",
            benefit: currentPackage.benefit || [],
            time: currentPackage.time || "",
        },
        validationSchema: PackageSchema,
        onSubmit: handleDialogSave,
    });

    return (
        <form onSubmit={formik.handleSubmit}>
            <DialogContent>
                <TextField
                    fullWidth
                    margin="dense"
                    variant="outlined"
                    id="title"
                    name="title"
                    label="Title"
                    value={formik.values.title}
                    onChange={formik.handleChange}
                    error={formik.touched.title && Boolean(formik.errors.title)}
                    helperText={formik.touched.title && formik.errors.title}
                />
                <TextField
                    fullWidth
                    margin="dense"
                    variant="outlined"
                    id="price"
                    name="price"
                    label="Price"
                    type="number"
                    value={formik.values.price}
                    onChange={formik.handleChange}
                    error={formik.touched.price && Boolean(formik.errors.price)}
                    helperText={formik.touched.price && formik.errors.price}
                />
                <TextField
                    fullWidth
                    margin="dense"
                    variant="outlined"
                    id="time"
                    name="time"
                    label="Time"
                    value={formik.values.time}
                    onChange={formik.handleChange}
                    error={formik.touched.time && Boolean(formik.errors.time)}
                    helperText={formik.touched.time && formik.errors.time}
                />
                <TextField
                    fullWidth
                    margin="dense"
                    variant="outlined"
                    id="benefit"
                    name="benefit"
                    label="Benefits (comma separated)"
                    value={Array.isArray(formik.values.benefit) ? formik.values.benefit.join(", ") : ""}
                    onChange={formik.handleChange}
                    error={formik.touched.benefit && Boolean(formik.errors.benefit)}
                    helperText={formik.touched.benefit && formik.errors.benefit}
                />
            </DialogContent>
            <DialogActions>
                <Button onClick={handleClose}>Cancel</Button>
                <Button type="submit" disabled={formik.isSubmitting}>
                    {currentPackage.id ? "Save" : "Add"}
                </Button>
            </DialogActions>
        </form>
    );
}

export default Packages;
